package com.syntax.class1.introduction;

public class PrimitiveDataTypes {
    public static void main(String[] args) {
        // TODO Auto-generated method stub

        //Storing and representing whole number values
        byte box1 =127;
        short box2 = 32767;
        int box3 = 100000; //popular
        long box4 =106558585585858L; //credit cards, transaction

        //Storing and representing decimal values
        float variable1=1.99F;
        double variable2=99.0; //popular

        //Storing Single characters
        char container='A';
        char dollar='$';

        //Storing yes or no values
        boolean hot=true;
        boolean tired=false;



        //System.out.println("variable2"); //prints text variable2

        System.out.println(box1); //Prints value of the variable
        System.out.println(box2);
        System.out.println(box3);
        System.out.println(box4);
        System.out.println(variable1);
        System.out.println(variable2); //Prints value of the variable
        System.out.println(container);
        System.out.println(dollar);
        System.out.println(hot);
        System.out.println(tired);
        //System.out.println(variable7); //error because this variable does not exist
    }
}
